package com.project.myapps.movie.controllers;import com.project.myapps.movie.entities.Movie;import com.project.myapps.movie.services.MovieService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1")public class MovieController {    @Autowired    private MovieService movieService;    @PostMapping("/addMovie")    public String addMovie(@RequestBody Movie movies){        return movieService.addMovie(movies);    }}