package com.project.myapps.student.controller;import com.project.myapps.student.model.StudentRequest;import com.project.myapps.student.service.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1")public class StudentController {    @Autowired    private StudentService studentService;    @PostMapping("/addStudent")    public String addStudent(@RequestBody StudentRequest request) {        return studentService.save(request);    }}